import styled from '@emotion/styled';

interface ImageProps {
  src: string;
  width: number;
  height: number;
  ratio?: 'square' | number;
  radius?: 'circle' | number;
  alt?: string;
}

const Image = ({
  src,
  width,
  height,
  ratio = 'square',
  radius,
  alt = '',
  ...props
}: ImageProps) => {
  const style: React.CSSProperties = { width };

  const ratioType = (ratio: 'square' | number, width?: number, height?: number) => {
    if (ratio === 'square')
      return 1 / 1;
    else if (typeof ratio === 'number')
      return ratio;
    else if (width && height)
      return width / height;
    return 1;
  };

  if (radius === 'circle') {
    style.borderRadius = '50%';
  } else if (typeof radius === 'string' && !Number.isNaN(Number(radius))) {
    style.borderRadius = `${Number(radius)}px`;
  } else if (typeof radius === 'number') {
    style.borderRadius = `${radius}px`;
  }

  return (
    <StyledImage
      src={src}
      style={style}
      alt={alt}
      ratio = {ratioType(ratio, width, height)}
      {...props} /> 
  );
};

const StyledImage = styled.img<{ ratio: number }>`
  display: block;
  max-width: 100%;
  height: auto;
  aspect-ratio: ${({ ratio }) => ratio};
`;

export default Image;
