import styled from '@emotion/styled';

const categories = [
  { key: 'birthday', name: '생일', image: 'url/to/birthday.png' },
  { key: 'graduation', name: '졸업선물', image: 'url/to/graduation.png' },
  { key: 'luxury', name: '스몰럭셔리', image: 'url/to/luxury.png' },
  { key: 'famous', name: '명품선물', image: 'url/to/famous.png' },
  { key: 'wedding', name: '결혼/집들이', image: 'url/to/wedding.png' },
  { key: 'warm', name: '따뜻한선물', image: 'url/to/warm.png' },
  { key: 'light', name: '가벼운선물', image: 'url/to/light.png' },
  { key: 'affordable', name: '팬심저격', image: 'url/to/affordable.png' },
  { key: 'voucher', name: '교환권', image: 'url/to/voucher.png' },
  { key: 'health', name: '건강/비타민', image: 'url/to/health.png' },
  { key: 'fruit', name: '과일/한우', image: 'url/to/fruit.png' },
  { key: 'kids', name: '출산/키즈', image: 'url/to/kids.png' },
];

const CategorySection = () => {
  return (
    <CategoryContainer>
      {categories.map((category) => (
        <CategoryItem key={category.key}>
          <Image src={category.image} alt={category.name} />
          <CategoryName>{category.name}</CategoryName>
        </CategoryItem>
      ))}
    </CategoryContainer>
  );
};

const CategoryContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  padding: 20px;
`;

const CategoryItem = styled.div`
  margin: 10px;
  text-align: center;
`;

const Image = styled.img`
  width: 80px;
  height: 80px;
  border-radius: 10px;
  margin-bottom: 10px;
`;

const CategoryName = styled.p`
  font-size: 14px;
`;

export default CategorySection;
