import styled from "@emotion/styled";

interface InputProps {
  type?: 'text' | 'number';
  placeholder?: string;
  value?: string;
  onChange?: (e: React.ChangeEvent<HTMLInputElement>) => void;
  size?: 'small' | 'medium' | 'large' | 'responsive';
  disabled?: boolean;
  invalid?: boolean;
}

export const Input = ({
  type = 'text',
  placeholder = '',
  value = '',
  onChange,
  size = 'medium',
  disabled = false,
  invalid = false,
  ...props
}: InputProps) => {
  const sizeClass = size === 'responsive' ? 'storybook-input--responsive' : `storybook-input--${size}`;
  const inputClassName = ['storybook-input', sizeClass, invalid ? 'storybook-input--invalid' : ''].join(' ');

  return (
    <StyledInput>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className={inputClassName}
        disabled={disabled}
        {...props}
      />
      {invalid && <p className="error-message error-message--invalid">잘못된 입력입니다.</p>}
    </StyledInput>
  );
};

const StyledInput = styled.input `
  .storybook-input {
    padding: 0.5rem;
    border: 1px solid #ccc;
    border-radius: 4px;
    font-size: 1rem;
    width: 100%;
  }

  .storybook-input--small {
    font-size: 0.75rem;
    padding: 0.25rem;
  }

  .storybook-input--medium {
    font-size: 1rem;
    padding: 0.5rem;
  }

  .storybook-input--large {
    font-size: 1.25rem;
    padding: 0.75rem;
  }

  @media (max-width: 600px) {
    .storybook-input--responsive {
      font-size: 0.75rem;
      padding: 0.25rem;
    }
  }

  @media (min-width: 601px) and (max-width: 900px) {
    .storybook-input--responsive {
      font-size: 1rem;
      padding: 0.5rem;
    }
  }

  @media (min-width: 901px) {
    .storybook-input--responsive {
      font-size: 1.25rem;
      padding: 0.75rem;
    }
  }

  .storybook-input--disabled {
    opacity: 0.6;
  }

  .error-message {
    display: none;
  }

  .error-message--invalid {
    color: red;
    display: block;
  }
`;
