import { Meta, StoryObj } from '@storybook/react';
import Image from '@components/Image/Image';

export default {
  title: 'Components/Image',
  component: Image,
  argTypes: {
    src: { control: 'text' },
    width: { control: { type: 'number' } },
    height: { control: { type: 'number' } },
    ratio: { control: 'text' },
    radius: { control: { type: 'number' } },
    alt: { control: 'text' },
  },
} as Meta<typeof Image>;

type Story = StoryObj<typeof Image>;

const validateRatio = (ratio: string | number): 'square' | number => {
  if (ratio === 'square') {
    return 'square';
  }
  const parsed = parseFloat(ratio as string);
  if (!isNaN(parsed)) {
    return parsed;
  }
  throw new Error('Invalid ratio value');
};

export const Default: Story = {
  args: {
    src: 'https://t1.kakaocdn.net/friends/www/talk/kakaofriends_talk_2018.png',
    alt: 'Placeholder Image',
    width: 150,
    height: 150,
    ratio: validateRatio('square'), // 초기값 검증
    radius: 10,
  },
  argTypes: {
    ratio: {
      control: 'text',
      type: 'string',
      validate: validateRatio, // 입력값 검증
    },
  },
};
