import { useState } from 'react';

interface Item {
  id: number;
  rank: number;
  corp: string;
  name: string;
  price: number;
  image: string;
}

const useFilter = (items: Item[]) => {
  const [filter, setFilter] = useState('all');
  const [subFilter, setSubFilter] = useState('받고 싶어한');

  const filterItems = () => {
    let filteredItems = items;

    // '전체'와 동일한 아이템 목록을 유지하기 위해 필터 조건마다 전체 아이템을 반환
    if (filter === 'female' || filter === 'male' || filter === 'teen') {
      filteredItems = items;
    }

    if (subFilter === '받고 싶어한' || subFilter === '많이 선물한' || subFilter === '위시로 받은') {
      filteredItems = filteredItems;
    }

    return filteredItems;
  };

  return { filter, setFilter, subFilter, setSubFilter, filterItems };
};

export default useFilter;
