// import './grid.css';
import { css } from '@emotion/react';
import styled from '@emotion/styled';

interface GridProps {
  columns: number;
  gap: number;
  children: React.ReactNode;
}

const Grid = ({ columns, gap, children }: GridProps) => {
  return (
    <div className="grid" style={{ gridTemplateColumns: `repeat(${columns}, 1fr)`, gap: `${gap}px` }}>
      {children}
    </div>
  );
};

const StyledGrid = styled.div`
  display: grid;
  width: 100%;
  height: 100%;

  &.grid-item {
    text-align: center;
    font-size: 14px;
  }
`;

export default Grid;
