import './button.css'

interface ButtonProps {
  primary?: boolean;
  backgroundColor?: string;
  size?: 'small' | 'medium' | 'large'| 'responsive';
  label: string;
  onClick?: () => void;
  theme?: 'Con' | 'Muji' | 'JayG';
}

export const Button = ({
  primary = false,
  size = 'medium',
  backgroundColor = '',
  label,
  theme,
  ...props
}: ButtonProps) => {

  let themeType = '';
  switch (theme) {
    case 'Con':
      themeType = 'button--con';
      break;
    case 'Muji':
      themeType = 'button--muji';
      break;
    case 'JayG':
      themeType = 'button--jayg';
      break;
    default:
      themeType = '';
  }

  // mode 변수에서 결정한 primary prop의 값에 따라 CSS 적용
  const mode = primary ? 'button--primary' : 'button--secondary';
  const sizeClass = size === 'responsive' ? 'button--responsive' : `button--${size}`;
  return (
    <button
      type="button"
      className={['button', sizeClass, mode, themeType].join(' ')}
      style={{ backgroundColor }}
      {...props}
    >
      {label}
    </button>
  );
};
