import styled from '@emotion/styled';
import GoodsItem from '@components/GoodsItem/GoodsItem';
import { useState } from 'react';

const GoodsImageLink = "https://st.kakaocdn.net/product/gift/product/20231030175450_53e90ee9708f45ffa45b3f7b4bc01c7c.jpg";

// 21개의 상품을 생성
const items = Array.from({ length: 21 }, (_, index) => ({
  id: index + 1,
  rank: index + 1,
  corp: 'BBQ',
  name: `BBQ 양념치킨+크림치즈볼+콜라1.25L`,
  price: 29000,
  image: GoodsImageLink,
}));

const ItemList = () => {
  const [showMore, setShowMore] = useState(false);
  const displayedItems = showMore ? items : items.slice(0, 6);

  return (
    <>
      <ItemsContainer>
        {displayedItems.map(item => (
          <GoodsItem
            key={item.id}
            subtitle={item.corp}
            title={item.name}
            imageSrc={item.image}
            amount={item.price}
            rankingIndex={item.rank}
          />
        ))}
      </ItemsContainer>
      <ButtonContainer>
        <MoreButton onClick={() => setShowMore(!showMore)}>
          {showMore ? '접기' : '더보기'}
        </MoreButton>
      </ButtonContainer>
    </>
  );
};

const ItemsContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
`;

const ButtonContainer = styled.div`
  text-align: center;
  margin-top: 20px;
`;

const MoreButton = styled.button`
  width: 400px; 
  height: 35px; 
  border: 1px solid #ccc; 
  border-radius: 5px; 
  background-color: #fff; 
  color: #333; 
  cursor: pointer;
  font-size: 16px; 

  &:hover {
    background-color: #f9f9f9;
  }
`;

export default ItemList;
