import styled from '@emotion/styled';

interface ImageProps {
  src: string;
  width: number;
  height: number;
  ratio?: 'square' | number;
  radius?: 'circle' | number;
  alt?: string;
}

const Image = ({
  src,
  width,
  height,
  ratio = 'square',
  radius,
  alt = '',
  ...props
}: ImageProps) => {
  const style: React.CSSProperties = { width };

const ratioType = (ratio: 'square' | number, width?: number, height?: number) => {
  if (ratio === 'square')
    return 1;
  if (typeof ratio === 'number')
    return ratio;
  if (width && height)
    return width/height;
  return 1;
};
const calculatedRatio = ratioType(ratio, width, height);

  if (radius === 'circle') {
    style.borderRadius = '50%';
  } else if (typeof radius === 'string' && !Number.isNaN(Number(radius))) {
    style.borderRadius = `${Number(radius)}px`;
  } else if (typeof radius === 'number') {
    style.borderRadius = `${radius}px`;
  }

  return (
    <StyledImage
      src = {src}
      width = {width}
      height = {height}
      calculatedRatio = {calculatedRatio}
      radius = {radius}
      alt = {alt}
      {...props} /> 
  );
};

const StyledImage = styled.img<{ width: number, height: number, calculatedRatio: number, radius?: 'circle' | number }>`
  display: block;
  max-width: 100%;
  width: ${({ width }) => `${width}px`};
  height: ${({ height }) => `${height}px`};
  aspect-ratio: ${({ calculatedRatio }) => calculatedRatio};
  ${({ radius }) => radius === 'circle' && `border-radius: 50%;`}
  ${({ radius }) => typeof radius === 'number' && `border-radius: ${radius}px;`}
`;

export default Image;
