import styled from '@emotion/styled';

interface ImageProps {
  src: string;
  width: number;
  ratio?: 'auto' | 'square' | number;
  radius?: 'circle' | number;
  alt?: string;
}

const Image = ({
  src,
  width,
  ratio = 'auto',
  radius,
  alt = '',
  ...props
}: ImageProps) => {

  const ratioType = (ratio: 'auto' | 'square' | number): string => {
    if (ratio === 'square') return '1 / 1';
    if (typeof ratio === 'number') {
      return ratio.toString().includes('/') ? ratio.toString() : `${ratio} / 1`;
    }
    return 'auto';
  };

  const borderRadius = (): string => {
    if (radius === 'circle') return '50%';
    if (typeof radius === 'number') return `${radius}px`;
    return '0';
  };

  return (
    <StyledImage
      src={src}
      width={width}
      height='auto'
      ratio={ratioType(ratio)}
      borderRadius={borderRadius()}
      alt={alt}
      {...props}
    /> 
  );
};

const StyledImage = styled.img<{ width: number; height: string; ratio: string; borderRadius: string }>`
  display: block;
  width: ${({ width }) => width}px;
  height: ${({ height }) => height};
  object-fit: cover;
  object-position: center;
  aspect-ratio: ${({ ratio }) => ratio};
  border-radius: ${({ borderRadius }) => borderRadius};
`;

export default Image;
