import { useState } from 'react';

interface Item {
  id: number;
  rank: number;
  corp: string;
  name: string;
  price: number;
  image: string;
}

const useFilter = (items: Item[]) => {
  const [filter, setFilter] = useState('all');
  const [subFilter, setSubFilter] = useState('받고 싶어한');

  const filterItems = () => {
    let filteredItems = items;

    if (filter === 'female')
      filteredItems = items.filter(item => item.corp === '여성이');
    else if (filter === 'male')
      filteredItems = items.filter(item => item.corp === '남성이');
    else if (filter === 'teen')
      filteredItems = items.filter(item => item.corp === '청소년이');

    if (subFilter === '많이 선물한')
      filteredItems = filteredItems.sort((a, b) => a.rank - b.rank);
    else if (subFilter === '위시로 받은')
      filteredItems = filteredItems.sort((a, b) => a.rank - b.rank);

    return filteredItems;
  };

  return { filter, setFilter, subFilter, setSubFilter, filterItems };
};

export default useFilter;
